    try{
      let roles = await Rol.query()
      .select('id', 'name')
      .where('state', true)
      return response.status(200).json({status: true ,message: 'Roles obtenidos con exito', roles})
    }
    catch{
      return response.status(400).json({status: false ,message: 'No se pudieron obtener los roles'})
