import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import UserController from 'App/Controllers/Http/UsersController'
import User from 'App/Models/User'

export default class Admin {
  public async handle(ctx: HttpContextContract, next: () => Promise<void>) {
    const authorizationHeader = ctx.request.header('authorization')

    try{

      if (authorizationHeader == undefined) {
        throw new Error('No se encontró el token de autenticación.')
      }


      const userController = new UserController()

      const {id} = await userController.obtenerPayload(authorizationHeader)
      const user = await User.find(id)

      if(!user){
        throw new Error('No se encontró el usuario.')
      }

      if(user.role != 1){
        throw new Error('No tiene permisos para realizar esta acción.')
      }
      await next()

    }
    catch(error){
      return ctx.response.status(401).send({
        message: 'Token inválido',
        status: 401
      })
    }



  }
}

import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import Question from 'App/Models/Question'
import AnswersController from './AnswersController'

export default class QuestionsController {

  public async createQuestion({ request, response }: HttpContextContract) {
    try{
    const data = request.input('question')
    const opciones = request.input('options')

      let question = await Question.create({
        question: data,
        state: true
      })

      let answer = new AnswersController()

      opciones.forEach((element: any) => {
        answer.createAnswer(question.id, element)
      });

      return response.status(200).json({state: true ,message: 'Pregunta creada correctamente'})
    }
    catch(error){
      return response.status(400).json({state: false ,message: 'Error al crear la pregunta'})
    }
  }

  public async getQuestions({ response }: HttpContextContract) {
    try{
      let question = await Question.query().select('question', 'id').where('state', true)
      return response.status(200).json({state: true ,questions: question})
    }catch(error){
      return response.status(400).json({state: false ,message: 'Error al listar las preguntas'})
    }

  }

  public async deleteQuestion({ params, response }: HttpContextContract) {
    try{
      let question = await Question.find(params.id)
      if(!question){
        throw new Error('Pregunta no encontrada')
      }
      if(question){
        question.state = false
        await question.save()
        return response.status(200).json({state: true ,message: 'Pregunta Eliminada con exito'})
      }
    }catch(error){
      console.log(error);
      return response.status(400).json({state: false ,message: 'Error al eliminar la pregunta'})
    }
  }

  public async updateQuestion({ params, request, response }: HttpContextContract) {
    try{
      let question = await Question.find(params.id)
      if(!question){
        throw new Error('Pregunta no encontrada')
      }

      question.question = await request.input('question')
      await question.save()
      console.log('se ejecuto esta parte del codigo');
      return response.status(200).json({state: true ,message: 'Pregunta Editada con exito .'})
    }
    catch(error){
      console.log(error);
      return response.status(400).json({state: false ,message: 'Error al editar la pregunta'})
    }
  }

}

import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import Answer from "App/Models/Answer"
import Question from "App/Models/Question"
export default class AnswersController {

  public async createAnswer(id: number, data: any){
      await Answer.create({
        answer: data.opcion,
        is_correct: data.iscorrect,
        state: true,
        questionId: id
      })
  }

  public async getAnswers({params, response}: HttpContextContract){
    try{
      const answers = await Answer.query().select('id', 'answer').where('questionId', params.id)
      if(answers.length == 0){
        throw new Error('No se encontraron opciones')
      }

      const question = await Question.find(params.id)

      if (question?.state == false){
        throw new Error('Pregunta inactiva')
      }

      const options = answers.map((answer: any) => {
        return {
          id: answer.id,
          option: answer.answer
        }
      })

      return response.status(200).json({state: true ,message: 'Listado de opciones' ,options: options})
    }catch(error){
      return response.status(404).json({state: false ,message: 'Error al obtener el listado de opciones'})
    }

  }

  public async updateAnswer({request, response, params}: HttpContextContract){
    const data = request.only(['opcion', 'iscorrect'])

    try{
      const answer = await Answer.find(params.id)

      if(!answer){
        throw new Error('Opcion no encontrada')
      }

      answer.answer = data.opcion
      answer.is_correct = data.iscorrect
      await answer.save()
      return response.status(200).json({status: true ,message: 'opcion Editada con exito'})
    }catch(error){
      return response.status(404).json({status: false ,message: 'Error al editar la opcion'})
    }
  }




}

